mapper.select:
select * from dish

    name like concat('%',#{name},'%')
    category_id = #{categoryId}
    status = #{status}

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, category_id = #{categoryId}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.delete:
delete from dish
where id in

    #{id}
